import lift from './res/girl-lift.png'
import railing from './res/railing.png'
import sky from './res/sky.png'
import terrace from './res/terrace.jpg'
import './App.css';
import { Parallax, ParallaxLayer } from '@react-spring/parallax';
import React, { useState, useEffect } from 'react';
import styles from './styles.module.css'

function App() {
  const [sticky, setSticky] = useState(() => ({
    start: 0.45,
    end: 4
  }));


  // Add an event listener to update the state when window is resized
  useEffect(() => {
    const handleResize = () => {
      if (window.innerWidth <= 768) {
        console.log("Window size changed: <= 768");
        setSticky({ start: -2.2, end: 4 });
        // console.log("<=768",sticky)
      } else {
        console.log("Window size changed: > 768");
        setSticky({ start: 0.45, end: 4 });
        // console.log(">768",sticky)
      }
    };

    window.addEventListener('resize', handleResize);

    // Clean up the event listener when component unmounts
    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  useEffect(() => {
    console.log("Sticky state updated:", sticky);
  }, [sticky]);


  return (
    <div className="flex-container">
      <Parallax pages={4}>
        {/* // page 1 */}
        <div className="flex-item1">
          <ParallaxLayer
            offset={0}
            speed={1}
            factor={1}
            style={{
              backgroundImage: `url(${terrace})`,
              backgroundSize: '100%'
            }}
          ></ParallaxLayer>
        </div>
        {/* //elevator */}
        <div className="flex-item2">
          <ParallaxLayer
            sticky={sticky}
            offset={0}
            speed={1}
            factor={1}
            style={{ textAlign: 'left', flex: 2 }}
          >
            <div className="card">
              <img src={lift} className="sticky-image" style={{ maxWidth: '100%', height: 'auto' }} />
            </div>
          </ParallaxLayer>
        </div>
        {/* // page 2 */}
        <ParallaxLayer
          offset={1}
          speed={1}
          factor={1}
          style={{
            backgroundImage: `url(${sky})`,
            backgroundSize: 'cover',
          }}
        ></ParallaxLayer>

        {/* // page 3 */}
        <ParallaxLayer
          offset={2}
          speed={1}
          factor={1}
          style={{
            backgroundImage: `url(${sky})`,
            backgroundSize: 'cover',
          }}
        ></ParallaxLayer>

      </Parallax>
    </div>
  );
}

export default App;

