  const alignCenter = { display: 'flex', alignItems: 'center' }

  return (
    <div className="flex-container">
      <Parallax pages={3}>
        {/* // page 1 */}
        <div className="flex-item1">
          <ParallaxLayer
            offset={0}
            speed={0}
            factor={3}
            style={{
              backgroundImage: `url(${sky})`,
              backgroundSize: 'cover',
            //   ...alignCenter,
            // justifyContent: 'flex-end',
            }}
          ></ParallaxLayer>
        </div>


        <ParallaxLayer sticky={{ start: 0.4, end: 5 } }>
          <div className= "card" >
          <img src={lift} style={{ maxWidth: '100%', height: 'auto' }} />
          </div>
        </ParallaxLayer>
        <ParallaxLayer
          offset={0.35}
          speed={0.1}
          >
          <div style={{ display: 'inline-block', marginLeft:'45%'}}>
           <HeroSection/>
          </div>
        </ParallaxLayer>

        <ParallaxLayer
          offset={1}
          speed={0.1}
          >
          <div style={{ display: 'inline-block', marginLeft:'45%'}}>
          <h1>Experience</h1>
           <Internship/>
          </div>
        </ParallaxLayer>

        <ParallaxLayer
          offset={2}
          speed={0.1}
          >
          <div style={{ display: 'inline-block', marginLeft:'45%'}}>
            <div style={{ marginBottom: '20px' }}> {/* Add margin to the second Internship component */}
            <h1>Projects</h1>
              <Internship/>
            </div>
            <Internship/>
          </div>
        </ParallaxLayer>

        <ParallaxLayer offset={0} speed={0.8} style={{ opacity: 0.2 }}>
          <img src={url('cloud')} style={{ display: 'block', width: '20%', marginLeft: '55%' }} />
          <img src={url('cloud')} style={{ display: 'block', width: '10%', marginLeft: '15%' }} />
        </ParallaxLayer>

        <ParallaxLayer offset={0.75} speed={0.5} style={{ opacity: 0.1 }}>
          <img src={url('cloud')} style={{ display: 'block', width: '20%', marginLeft: '70%' }} />
          <img src={url('cloud')} style={{ display: 'block', width: '20%', marginLeft: '40%' }} />
        </ParallaxLayer>

        <ParallaxLayer offset={0} speed={0.2} style={{ opacity: 0.2 }}>
          <img src={url('cloud')} style={{ display: 'block', width: '10%', marginLeft: '10%' }} />
          <img src={url('cloud')} style={{ display: 'block', width: '20%', marginLeft: '75%' }} />
        </ParallaxLayer>

        <ParallaxLayer offset={0.6} speed={-0.1} style={{ opacity: 0.4 }}>
          <img src={url('cloud')} style={{ display: 'block', width: '20%', marginLeft: '60%' }} />
          <img src={url('cloud')} style={{ display: 'block', width: '25%', marginLeft: '30%' }} />
          <img src={url('cloud')} style={{ display: 'block', width: '10%', marginLeft: '80%' }} />
        </ParallaxLayer>

        <ParallaxLayer offset={1.6} speed={0.4} style={{ opacity: 0.6 }}>
          <img src={url('cloud')} style={{ display: 'block', width: '20%', marginLeft: '5%' }} />
          <img src={url('cloud')} style={{ display: 'block', width: '15%', marginLeft: '75%' }} />
        </ParallaxLayer>






        {/* <div className="flex-item2">
          <ParallaxLayer
            sticky={sticky}
            offset={0}
            speed={1}
            factor={1}
            style={{ textAlign: 'left', flex: 2 }}
          >
            <div className="card">
              <img src={lift} className="sticky-image" style={{ maxWidth: '100%', height: 'auto' }} />
            </div>
          </ParallaxLayer>
        </div> */}
        {/* // page 2 */}
        {/* <ParallaxLayer
          offset={2}
          speed={0}
          factor={1}
          style={{
            backgroundImage: `url(${land})`,
            backgroundSize: 'cover',
          }}
        ></ParallaxLayer> */}

        {/* // page 3 */}
        {/* <ParallaxLayer
          offset={2}
          speed={1}
          factor={1}
          style={{
            backgroundImage: `url(${sky})`,
            backgroundSize: 'cover',
          }}
        ></ParallaxLayer> */}

      </Parallax>
    </div>
  );
}

export default App;

