import './App.css';
import { Parallax, ParallaxLayer } from '@react-spring/parallax';
import React, { useState, useEffect , useRef} from 'react';
import { HeroSection } from './HeroSection';
import InfoCards from './InfoCards';
import newsand from './new_sand.png'
import girl from './girl1.png';
import gs from './goldmansachs_logo.png';
import zipcar from './zipcar_fb.jpg';
import furball from './furball.jpg';
import chatbot from './chatbot.jpg';

const url = (name, wrap = false) =>
  `${wrap ? 'url(' : ''}https://awv3node-homepage.surge.sh/build/assets/${name}.svg${wrap ? ')' : ''}`
function App() {
  const [pages, setPages] = useState(3.5);
  const pagesRef = useRef(pages);

  useEffect(() => {
    pagesRef.current = pages;
  }, [pages]);


  useEffect(() => {
    const updatePages = () => {
        if (window.innerWidth < 900) {
          setPages(6.0);
          console.log(pages);
        } else {
            setPages(3.5);
        }
    };

    // Initial check
    updatePages();

    // Add event listener
    window.addEventListener('resize', updatePages);

    // Cleanup
    return () => window.removeEventListener('resize', updatePages);
  }, []);


  const internships = [
    {
      title: "Goldman Sachs",
      body: "Developed a web application to decommission a legacy trading system in the Asset Management team.|Developed REST APIs for retrieving shares, facilitating transactions, and ensuring timely deployment through streamlined teamwork and coordination.|Utilized Agile methodology and collaborated with traders to achieve consensus on design direction",
      techstack: "Languages/Tools: Java, Spring Boot, Maven, Angular, SQL, Git.",
      image: gs,
      // height: "500"
    },
    {
      title: "Zipcar",
      body: "Collaborated with Zipcar's billing team to develop features including new search options, on-time tolls and violations updates on invoices and, automating critical billing tasks impacting over 10,000 customers.|Utilized Datadog to monitor billing services to ensure seamless operations,timely issue resolution and hosted grooming sessions for team.",
      techstack: "Languages/Tools: Java, Kotlin, Ruby, SQL, Git, Adyen, Prometheus.",
      image: zipcar,
      // height: "500"
    }
  ];
  const projects1 = [
    {
      title: "What’s in your Fridge",
      body: "Developed a web application that recommends personalized recipes based on ingredients in the fridge and user preferences, utilizing a content-based filtering recommendation engine",
      image: "goldman_sachs_image_url",
      // height: "350"
    },
    {
      title: "Furball Favs",
      body: "Developed an Android e-commerce app that enables pet owners to easily find and buy high-quality pet products with a seamless checkout process using Firebase authentication and storage",
      image: furball,
      // height: "350"
    }]
    const projects2 = [
    {
      title: "Cravewheels ChatBot",
      body: "Developed a multi-user chatbot using Open AI APIs to deliver seamless real-time interactions along with neural network-powered text-to-speech synthesis",
      image: chatbot,
      // height: "350"
    },{
      title: "Language Classifier",
      body: "Implemented a text classifier with an accuracy rate of 96% for determining the language of a text as either English or Dutch",
      image: "zipcar_image_url",
      // height: "350"
    }
  ];

  const alignCenter = { display: 'flex', alignItems: 'center' }

  return (
    <div className="flex-container">
      <Parallax pages={pages}>
        {/* setting background color */}
        <div className="flex-item1">
          <ParallaxLayer
            offset={0}
            speed={0}
            factor={pages}
            style={{ background: 'linear-gradient(to bottom, #7bb2ff, #e0ffff)'}}
          ></ParallaxLayer>
        </div>

        {/* Clouds */}
        <ParallaxLayer offset={0} speed={0.8} style={{ opacity: 0.2 }}>
          <img src={url('cloud')} style={{ display: 'block', width: '16%', marginLeft: '15%' }} />
          <img src={url('cloud')} style={{ display: 'block', width: '10%', marginLeft: '55%' }} />
        </ParallaxLayer>

        <ParallaxLayer offset={0.1} speed={0.8} style={{ opacity: 0.5 }}>
          <img src={url('cloud')} style={{ display: 'block', width: '13%', marginLeft: '78%' }} />
        </ParallaxLayer>

        <ParallaxLayer offset={0.6} speed={0.4} style={{ opacity: 0.4 }}>
          <img src={url('cloud')} style={{ display: 'block', width: '10%', marginLeft: '25% '}} />
          <img src={url('cloud')} style={{ display: 'block', width: '25%', marginLeft: '60%' }} />
        </ParallaxLayer>

        <ParallaxLayer offset={0.75} speed={0.5} style={{ opacity: 0.5 }}>
          <img src={url('cloud')} style={{ display: 'block', width: '8%', marginLeft: '40%' }} />
        </ParallaxLayer>

        <ParallaxLayer offset={1} speed={0.6} style={{ opacity: 0.5 }}>
          <img src={url('cloud')} style={{ display: 'block', width: '16%', marginLeft: '14%' }} />
          <img src={url('cloud')} style={{ display: 'block', width: '20%', marginLeft: '75%' }} />
        </ParallaxLayer>

        <ParallaxLayer offset={1.3} speed={0.7} style={{ opacity: 0.2 }}>
          <img src={url('cloud')} style={{ display: 'block', width: '22%', marginLeft: '30%' }} />
          <img src={url('cloud')} style={{ display: 'block', width: '17%', marginLeft: '58%' }} />
        </ParallaxLayer>

        <ParallaxLayer offset={1.6} speed={0.7} style={{ opacity: 0.6 }}>
          <img src={url('cloud')} style={{ display: 'block', width: '10%', marginLeft: '75%' }} />
          <img src={url('cloud')} style={{ display: 'block', width: '15%', marginLeft: '9%' }} />
        </ParallaxLayer>

        <ParallaxLayer offset={1.9} speed={0.7} style={{ opacity: 0.3 }}>
