import * as React from "react";
import Card from "@mui/material/Card";
import CardHeader from "@mui/material/CardHeader";
import CardMedia from "@mui/material/CardMedia";
import CardContent from "@mui/material/CardContent";
import Typography from "@mui/material/Typography";
import Grid from "@mui/material/Grid"; 

export default function InfoCards(props) {
  return (
    <Grid container spacing={{ xs: 4, sm: 8, md: 12, lg: 20 }} style={{ zIndex: 0, position: 'relative' }}>
      {props.info.map((info, index) => (
        <Grid item xs={12} sm={12} md={6} lg={6} key={index} style={{ display: 'flex' }}>
          <Card
            sx={{
              width: 300,
              // height: info.height 
              opacity: 0.8
            }}
          >
            <CardHeader title={info.title} />
            <CardMedia
              component="img"
              height="130"
              image={info.image}
              alt={info.title}
              style={{ borderTop: '1px solid black', borderBottom: '1px solid black' }}
            />
            <CardContent>
              <Typography component={'span'} variant="body2" color="text.secondary">
              <ul>
                {info.body.split('|').map((item, index) => (
                  <li key={index}>{item.trim()}</li>
                ))}
              </ul>
                <br/>
                <strong style={{ fontWeight: 'bold' }}>{info.techstack}</strong>
              </Typography>
            </CardContent>
          </Card>
        </Grid>
      ))}
    </Grid>
  );
  
}
