import lift from './res/girl-lift.png'
import railing from './res/railing.png'
import sky from './res/sky.png'
import terrace from './res/terrace.jpg'
import './App.css';
import { Parallax, ParallaxLayer } from '@react-spring/parallax';
import styles from './styles.module.css'
import React, { Component } from 'react';

class App extends Component{
  constructor() {
    constructor(); {
      this.state = {
        sticky: { start: 0.2, end: 2.5 }, // Default sticky values
      };
    }

    componentDidMount(); {
      // Add a listener for screen width changes
      window.addEventListener('resize', this.handleResize);
      this.handleResize(); // Call it initially to set the appropriate sticky values
    }

    componentWillUnmount(); {
      // Remove the listener when the component unmounts
      window.removeEventListener('resize', this.handleResize);
    }

    handleResize = () => {
      // Adjust sticky values based on screen width
      if (window.innerWidth <= 768) {
        this.setState({
          sticky: { start: 0.1, end: 2 }, // Adjust values for smaller screens
        });
      } else {
        this.setState({
          sticky: { start: 0.2, end: 2.5 }, // Default values
        });
      }
    };
    return (
      <div class="flex-container">
        <Parallax pages={4}>
          {/* // page 1 */}
          <div class="flex-item1">
            <ParallaxLayer
              offset={0}
              speed={1}
              factor={1}
              style={{
                backgroundImage: `url(${terrace})`,
                backgroundSize: '100%'
              }}
            ></ParallaxLayer>
          </div>
          {/* //elevator */}
          <div class="flex-item2">
            <ParallaxLayer
              sticky={window.innerWidth < 768 ? { start: -1.0, end: 2.5 } : { start: 0.4, end: 2.5 }}
              offset={0}
              speed={1}
              factor={1}
              style={{ textAlign: 'left', flex: 2 }}
            >
              <div class="card">
                <img src={lift} className="sticky-image" style={{ maxWidth: '100%', height: 'auto' }} />
              </div>
            </ParallaxLayer>
          </div>
          {/* // page 2 */}
          <ParallaxLayer
            offset={1}
            speed={1}
            factor={1}
            style={{
              backgroundImage: `url(${sky})`,
              backgroundSize: 'cover',
            }}
          ></ParallaxLayer>

          {/* // page 3 */}
          <ParallaxLayer
            offset={2}
            speed={1}
            factor={1}
            style={{
              backgroundImage: `url(${sky})`,
              backgroundSize: 'cover',
            }}
          ></ParallaxLayer>

        </Parallax>
      </div>
    );
  }
}

export default App;

