import lift from './res/girl-lift.png'
import railing from './res/railing.png'
import sky from './res/sky.png'
import terrace from './res/terrace.jpg'
import './App.css';
import { Parallax, ParallaxLayer } from '@react-spring/parallax';
import React, { useState, useEffect } from 'react';
import styles from './styles.module.css'
import { HeroSection } from './HeroSection';

const url = (name, wrap = false) =>
  `${wrap ? 'url(' : ''}https://awv3node-homepage.surge.sh/build/assets/${name}.svg${wrap ? ')' : ''}`
function App() {
  // const [sticky, setSticky] = useState(() => ({
  //   start: 0.45,
  //   end: 4
  // }));


  // // Add an event listener to update the state when window is resized
  // useEffect(() => {
  //   const handleResize = () => {
  //     if (window.innerWidth <= 768) {
  //       console.log("Window size changed: <= 768");
  //       setSticky({ start: -2.2, end: 4 });
  //       // console.log("<=768",sticky)
  //     } else {
  //       console.log("Window size changed: > 768");
  //       setSticky({ start: 0.45, end: 4 });
  //       // console.log(">768",sticky)
  //     }
  //   };

  //   window.addEventListener('resize', handleResize);

  //   // Clean up the event listener when component unmounts
  //   return () => {
  //     window.removeEventListener('resize', handleResize);
  //   };
  // }, []);

  // useEffect(() => {
  //   console.log("Sticky state updated:", sticky);
  // }, [sticky]);

  const alignCenter = { display: 'flex', alignItems: 'center' }

  return (
    <div className="flex-container">
      <Parallax pages={4}>
        {/* // page 1 */}
        <div className="flex-item1">
          <ParallaxLayer
            offset={0}
            speed={0.5}
            factor={1}
            style={{
              backgroundImage: `url(${sky})`,
              backgroundSize: 'cover',
            //   ...alignCenter,
            // justifyContent: 'flex-end',
            }}
          ></ParallaxLayer>
        </div>

        <div className="container">
        <div className="row">
          <div className="col-md-6">
            <ParallaxLayer sticky={{ start: 0.4, end: 3 }}>
              <div className="card">
                <img src={lift} style={{ maxWidth: '100%', height: 'auto' }} />
              </div>
            </ParallaxLayer>
          </div>
          <div className="col-md-6">
            <ParallaxLayer offset={0.3} speed={0.1} style={{ display: 'block' }}>
              <HeroSection />
            </ParallaxLayer>
          </div>
        </div>
      </div>


        {/* //elevator */}
        <div className=''></div>
        <ParallaxLayer sticky={{ start: 0.4, end: 3 }}>
          <div className= "card" >
          <img src={lift} style={{ maxWidth: '100%', height: 'auto' }} />
          </div>
        </ParallaxLayer>
        <ParallaxLayer
          offset={0.3}
          speed={0.1}
          style={{ display: 'block', marginRight: '50%' }}>
          <HeroSection/>
        </ParallaxLayer>

        <ParallaxLayer offset={0} speed={0.8} style={{ opacity: 0.2 }}>
          <img src={url('cloud')} style={{ display: 'block', width: '20%', marginLeft: '55%' }} />
          <img src={url('cloud')} style={{ display: 'block', width: '10%', marginLeft: '15%' }} />
        </ParallaxLayer>

        <ParallaxLayer offset={0.75} speed={0.5} style={{ opacity: 0.1 }}>
          <img src={url('cloud')} style={{ display: 'block', width: '20%', marginLeft: '70%' }} />
          <img src={url('cloud')} style={{ display: 'block', width: '20%', marginLeft: '40%' }} />
        </ParallaxLayer>

        <ParallaxLayer offset={0} speed={0.2} style={{ opacity: 0.2 }}>
          <img src={url('cloud')} style={{ display: 'block', width: '10%', marginLeft: '10%' }} />
          <img src={url('cloud')} style={{ display: 'block', width: '20%', marginLeft: '75%' }} />
        </ParallaxLayer>

        <ParallaxLayer offset={0.6} speed={-0.1} style={{ opacity: 0.4 }}>
          <img src={url('cloud')} style={{ display: 'block', width: '20%', marginLeft: '60%' }} />
          <img src={url('cloud')} style={{ display: 'block', width: '25%', marginLeft: '30%' }} />
          <img src={url('cloud')} style={{ display: 'block', width: '10%', marginLeft: '80%' }} />
        </ParallaxLayer>

        <ParallaxLayer offset={1.6} speed={0.4} style={{ opacity: 0.6 }}>
          <img src={url('cloud')} style={{ display: 'block', width: '20%', marginLeft: '5%' }} />
          <img src={url('cloud')} style={{ display: 'block', width: '15%', marginLeft: '75%' }} />
        </ParallaxLayer>






        {/* <div className="flex-item2">
          <ParallaxLayer
            sticky={sticky}
            offset={0}
            speed={1}
            factor={1}
            style={{ textAlign: 'left', flex: 2 }}
          >
            <div className="card">
              <img src={lift} className="sticky-image" style={{ maxWidth: '100%', height: 'auto' }} />
            </div>
          </ParallaxLayer>
        </div> */}
        {/* // page 2 */}
        <ParallaxLayer
          offset={1}
          speed={0.5}
          factor={1}
          style={{
            backgroundImage: `url(${sky})`,
            backgroundSize: 'cover',
          }}
        ></ParallaxLayer>

        {/* // page 3 */}
        <ParallaxLayer
          offset={2}
          speed={1}
          factor={1}
          style={{
            backgroundImage: `url(${sky})`,
            backgroundSize: 'cover',
          }}
        ></ParallaxLayer>

      </Parallax>
    </div>
  );
}

export default App;

