// export default CloudFlexbox;
import React, { useState } from 'react';
import './CloudFlexbox.css';

function CloudFlexbox({ items, heading }) {
  const [showCarousel, setShowCarousel] = useState(window.innerWidth < 450);

  window.addEventListener('resize', () => {
    setShowCarousel(window.innerWidth < 450);
  });

  return (
    <div className={`cloud-container ${showCarousel ? 'carousel' : ''}`}>
      <h2 className="cloud-heading">{heading}</h2>
      {showCarousel ? (
        <div className="carousel-container">
          {items.map((item, index) => (
            <div key={index} className="carousel-item">
              <h3>{item.title}</h3>
              <p>{item.body}</p>
              <p>{item.techstack}</p>
            </div>
          ))}
        </div>
      ) : (
        items.map((item, index) => (
          <div key={index} className="flex-item">
            <h3>{item.title}</h3>
            <p>{item.body}</p>
            <p>{item.techstack}</p>
          </div>
        ))
      )}
    </div>
  );
}

export default CloudFlexbox;
